import React, { useState } from 'react';
import { Tweet } from './Tweet.js'
import './style.css'
import {data} from './Data';
function App() {
  const [tweets, setTweets] = useState(data);
  const doLike = (text) => {
    const tweet = tweets.findIndex(t => t.text == text)
    tweets[i].isLiked = !tweets[i].isLiked
    setTweets([...tweets]) 
  }
  return (   
    <div>
      {data.map((tweet, idx) => {
        return (
          <Tweet {...tweet} key={idx} doLike={doLike}/>
        )
      })}
    </div>
  );
}

export default App;
